// Validate required fields
const requiredFields = ['idea_description', 'business_context'];
const inputData = items[0].json;

for (const field of requiredFields) {
    if (!inputData[field] || inputData[field].trim() === '') {
        throw new Error(`Missing required field: ${field}`);
    }
}

// Clean and structure data
const processedInput = {
    ideaDescription: inputData.idea_description.trim(),
    businessContext: inputData.business_context.trim(),
    targetAudience: inputData.target_audience || 'General users',
    constraints: inputData.constraints || 'No specific constraints',
    timelinePreference: inputData.timeline_preference || '3-6 months'
};

return [{ json: processedInput }];

// Get the AI response
const aiResponse = items[0].json.output || items[0].json.text || items[0].json.response;

try {
    // Try to parse as JSON directly
    let ideationResult;
    if (typeof aiResponse === 'string') {
        // Clean the response - remove markdown formatting if present
        const cleanResponse = aiResponse.replace(/```json\n?|\n?```/g, '').trim();
        ideationResult = JSON.parse(cleanResponse);
    } else {
        ideationResult = aiResponse;
    }

    // Validate required fields
    const requiredFields = ['refined_idea', 'alternatives', 'core_problems', 'key_features', 'value_proposition'];
    for (const field of requiredFields) {
        if (!ideationResult[field]) {
            throw new Error(`Missing required field: ${field}`);
        }
    }

    // Ensure arrays are properly formatted
    if (!Array.isArray(ideationResult.alternatives)) ideationResult.alternatives = [];
    if (!Array.isArray(ideationResult.core_problems)) ideationResult.core_problems = [];
    if (!Array.isArray(ideationResult.key_features)) ideationResult.key_features = [];

    return [{
        json: {
            ...items[0].json,
            ideationResult: ideationResult
        }
    }];
} catch (error) {
    console.error('Error parsing ideation response:', error);
    throw new Error(`Failed to parse ideation agent response: ${error.message}`);
}

// Get AI response
const aiResponse =
    items[0].json.output ||
    items[0].json.text ||
    items[0].json.response;

if (!aiResponse) {
    throw new Error("AI response is empty");
}

/**
 * Extract JSON-like structure robustly
 * We don't trust the AI JSON — we rebuild our own.
 */

function extractValue(key, text) {
    // Extract string field: "key": "value..."
    const strRegex = new RegExp(`"${key}"\\s*:\\s*"(.*?)"`, "s");
    const matchStr = text.match(strRegex);
    if (matchStr) return matchStr[1].replace(/\n/g, " ");

    // Extract number field
    const numRegex = new RegExp(`"${key}"\\s*:\\s*(\\d+)`);
    const matchNum = text.match(numRegex);
    if (matchNum) return Number(matchNum[1]);

    return null;
}

function extractArrayOfObjects(key, text) {
    const regex = new RegExp(`"${key}"\\s*:\\s*\\[(.*?)\\]`, "s");
    const match = text.match(regex);
    if (!match) return [];

    const block = match[1];

    // Split into objects
    const objects = block.split("},").map(obj => obj + "}");

    return objects.map(objText => {
        return {
            name: extractValue("name", objText),
            strength: extractValue("strength", objText),
            weakness: extractValue("weakness", objText),
            market_share: extractValue("market_share", objText)
        };
    });
}

function extractArray(key, text) {
    const regex = new RegExp(`"${key}"\\s*:\\s*\\[(.*?)\\]`, "s");
    const match = text.match(regex);
    if (!match) return [];

    return match[1]
        .split(/",\s*"/)
        .map(s => s.replace(/(^")|("$)/g, "").trim());
}

// ---- Rebuild clean JSON ----

const marketResult = {
    market_size: extractValue("market_size", aiResponse),
    market_growth_rate: extractValue("market_growth_rate", aiResponse),
    competitors: extractArrayOfObjects("competitors", aiResponse),
    market_trends: extractArray("market_trends", aiResponse),
    opportunities: extractArray("opportunities", aiResponse),
    risks: extractArray("risks", aiResponse),
    attractiveness_score: extractValue("attractiveness_score", aiResponse) ?? 5,
    market_timing: extractValue("market_timing", aiResponse),
    market_summary: extractValue("market_summary", aiResponse)
};

// ---- Final response ----
return [
    {
        json: {
            ...items[0].json,
            marketResult
        }
    }
];

// Get the AI response
const aiResponse = items[0].json.output || items[0].json.text || items[0].json.response;

try {
    // Parse the JSON response
    let priorityResult;
    if (typeof aiResponse === 'string') {
        const cleanResponse = aiResponse.replace(/```json\n?|\n?```/g, '').trim();
        priorityResult = JSON.parse(cleanResponse);
    } else {
        priorityResult = aiResponse;
    }

    // Validate and normalize scores (must be 1-10)
    const scoreFields = ['business_value', 'user_impact', 'effort_required', 'risk_level', 'market_timing', 'strategic_fit'];
    for (const field of scoreFields) {
        if (typeof priorityResult[field] !== 'number' || 
            priorityResult[field] < 1 || 
            priorityResult[field] > 10) {
            priorityResult[field] = 5; // Default middle value
        }
    }

    // Recalculate priority score to ensure accuracy
    const calculatedScore = (
        priorityResult.business_value + 
        priorityResult.user_impact + 
        priorityResult.market_timing + 
        priorityResult.strategic_fit
    ) - (
        (10 - priorityResult.effort_required + 1) + // Invert effort (higher effort = lower score)
        (10 - priorityResult.risk_level + 1) // Invert risk (higher risk = lower score)
    );

    priorityResult.calculated_score = Math.round(calculatedScore);

    // Determine priority tier based on calculated score
    if (priorityResult.calculated_score >= 15) {
        priorityResult.priority_tier = "High";
    } else if (priorityResult.calculated_score >= 8) {
        priorityResult.priority_tier = "Medium";
    } else {
        priorityResult.priority_tier = "Low";
    }

    // Ensure arrays exist
    if (!Array.isArray(priorityResult.critical_success_factors)) {
        priorityResult.critical_success_factors = [];
    }

    // Validate ICE score object
    if (!priorityResult.ice_score || typeof priorityResult.ice_score !== 'object') {
        priorityResult.ice_score = {
            impact: priorityResult.user_impact,
            confidence: 5,
            ease: priorityResult.effort_required
        };
    }

    return [{
        json: {
            ...items[0].json,
            priorityResult: priorityResult
        }
    }];
} catch (error) {
    console.error('Error parsing prioritization response:', error);
    throw new Error(`Failed to parse prioritization agent response: ${error.message}`);
}

// Get the AI response
const aiResponse =
    items[0].json.output ||
    items[0].json.text ||
    items[0].json.response;

if (!aiResponse) {
    throw new Error("AI response is empty");
}

// ----- HELPERS -----
function extractArray(key, text) {
    const regex = new RegExp(`"${key}"\\s*:\\s*\\[(.*?)\\]`, "s");
    const match = text.match(regex);
    if (!match) return [];

    return match[1]
        .split(/",\s*"/)
        .map(s => s.replace(/(^")|("$)/g, "").trim());
}

function extractArrayOfObjects(key, text) {
    const regex = new RegExp(`"${key}"\\s*:\\s*\\[(.*?)\\]`, "s");
    const match = text.match(regex);
    if (!match) return [];

    const block = match[1];
    const objects = block.split("},").map(o => o + "}");

    return objects.map(obj => ({
        prerequisite: (obj.match(/"prerequisite"\s*:\s*"(.*?)"/) || [])[1] || "",
        dependent: (obj.match(/"dependent"\s*:\s*"(.*?)"/) || [])[1] || "",
        reason: (obj.match(/"reason"\s*:\s*"(.*?)"/) || [])[1] || ""
    }));
}

function extractString(key, text) {
    const regex = new RegExp(`"${key}"\\s*:\\s*"(.*?)"`, "s");
    const match = text.match(regex);
    return match ? match[1].replace(/\n/g, " ") : "";
}

// ----- REBUILD JSON SAFELY -----
const roadmapResult = {
    mvp_features: extractArray("mvp_features", aiResponse),
    mvp_timeline: extractString("mvp_timeline", aiResponse) || "3 months",
    mvp_success_metrics: extractArray("mvp_success_metrics", aiResponse),

    phase2_features: extractArray("phase2_features", aiResponse),
    phase2_timeline: extractString("phase2_timeline", aiResponse) || "6 months",
    phase2_success_metrics: extractArray("phase2_success_metrics", aiResponse),

    phase3_features: extractArray("phase3_features", aiResponse),
    phase3_timeline: extractString("phase3_timeline", aiResponse) || "12 months",
    phase3_success_metrics: extractArray("phase3_success_metrics", aiResponse),

    dependencies: extractArrayOfObjects("dependencies", aiResponse),

    critical_path: extractArray("critical_path", aiResponse),
    parallel_development: extractArray("parallel_development", aiResponse),

    team_requirements: {
        mvp_phase: extractArray("mvp_phase", aiResponse),
        scaling_phase: extractArray("scaling_phase", aiResponse)
    },

    roadmap_summary:
        "MVP (" +
        (extractString("mvp_timeline", aiResponse) || "3 months") +
        "): " +
        extractArray("mvp_features", aiResponse).length +
        " features"
};

// ----- RETURN -----
return [
    {
        json: {
            ...items[0].json,
            roadmapResult
        }
    }
];

