// Get the AI response
const aiResponse = items[0].json.output || items[0].json.text || items[0].json.response;

try {
    // Parse the JSON response
    let outputResult;
    if (typeof aiResponse === 'string') {
        const cleanResponse = aiResponse.replace(/```json\n?|\n?```/g, '').trim();
        outputResult = JSON.parse(cleanResponse);
    } else {
        outputResult = aiResponse;
    }

    // Validate required fields with defaults
    const requiredFields = {
        'executive_summary': 'Analysis completed for product opportunity assessment.',
        'recommendation': 'Further evaluation recommended.',
        'key_insights': [],
        'immediate_next_steps': [],
        'success_metrics': [],
        'decision_timeline': '2 weeks'
    };

    for (const [field, defaultValue] of Object.entries(requiredFields)) {
        if (!outputResult[field]) {
            outputResult[field] = defaultValue;
        }
    }

    // Ensure nested objects exist
    if (!outputResult.business_case || typeof outputResult.business_case !== 'object') {
        outputResult.business_case = {
            opportunity_size: 'Market opportunity assessment pending',
            competitive_advantage: 'Competitive positioning to be determined',
            resource_investment: 'Resource requirements analysis needed',
            expected_roi: 'ROI projection to be calculated'
        };
    }

    // Ensure arrays are properly formatted
    const arrayFields = ['key_insights', 'immediate_next_steps', 'success_metrics', 'risk_mitigation_plan'];
    for (const field of arrayFields) {
        if (outputResult[field] && !Array.isArray(outputResult[field])) {
            outputResult[field] = [];
        }
    }

    // Validate risk mitigation plan structure
    if (outputResult.risk_mitigation_plan && Array.isArray(outputResult.risk_mitigation_plan)) {
        outputResult.risk_mitigation_plan = outputResult.risk_mitigation_plan.map(risk => {
            if (typeof risk === 'string') {
                return {
                    risk: risk,
                    impact: 'Medium',
                    mitigation: 'Mitigation strategy to be defined',
                    owner: 'Product team'
                };
            }
            return risk;
        });
    }

    return [{
        json: {
            ...items[0].json,
            outputResult: outputResult
        }
    }];
} catch (error) {
    console.error('Error parsing output formatting response:', error);
    throw new Error(`Failed to parse output formatting agent response: ${error.message}`);
}

// Generate unique ID for this analysis session
const ideaId = `IDEA_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`;
const analysisDate = new Date().toISOString().split('T')[0];
const analysisTime = new Date().toISOString().split('T')[1].split('.')[0];

// Helper function to safely join arrays
const safeJoin = (arr, separator = '; ') => {
  if (!arr || !Array.isArray(arr)) return '';
  return arr.map(item =>
    typeof item === 'object' ? JSON.stringify(item) : String(item)
  ).join(separator);
};

// Helper function to extract nested object values
const extractNestedData = (obj, defaultValue = '') => {
  if (!obj || typeof obj !== 'object') return defaultValue;
  return Object.entries(obj)
    .map(([key, value]) => `${key}: ${value}`)
    .join('; ');
};

// Compile comprehensive results for Google Sheets
const finalOutput = {
  // Primary Identifiers
  idea_id: ideaId,
  analysis_date: analysisDate,
  analysis_time: analysisTime,

  // Core Product Information
  original_idea: $json.ideaDescription || '',
  refined_idea: $json.ideationResult?.refined_idea || '',
  value_proposition: $json.ideationResult?.value_proposition || '',
  target_audience: $json.targetAudience || '',
  business_context: $json.businessContext || '',

  // Ideation Results
  alternative_ideas: safeJoin($json.ideationResult?.alternatives),
  core_problems: safeJoin($json.ideationResult?.core_problems),
  key_features: safeJoin($json.ideationResult?.key_features),
  innovation_score: $json.ideationResult?.innovation_score || 0,

  // Market Analysis
  market_summary: $json.marketResult?.market_summary || '',
  market_size: $json.marketResult?.market_size || '',
  market_attractiveness_score: $json.marketResult?.attractiveness_score || 0,
  market_growth_rate: $json.marketResult?.market_growth_rate || '',
  market_timing: $json.marketResult?.market_timing || '',
  competitors: safeJoin($json.marketResult?.competitors?.map(c => `${c.name} (${c.strength})`)),
  market_trends: safeJoin($json.marketResult?.market_trends),
  market_opportunities: safeJoin($json.marketResult?.opportunities),
  market_risks: safeJoin($json.marketResult?.risks),

  // Prioritization Results
  priority_score: $json.priorityResult?.calculated_score || 0,
  priority_tier: $json.priorityResult?.priority_tier || 'Medium',
  business_value_score: $json.priorityResult?.business_value || 0,
  user_impact_score: $json.priorityResult?.user_impact || 0,
  effort_required_score: $json.priorityResult?.effort_required || 0,
  risk_level_score: $json.priorityResult?.risk_level || 0,
  market_timing_score: $json.priorityResult?.market_timing || 0,
  strategic_fit_score: $json.priorityResult?.strategic_fit || 0,
  success_probability: $json.priorityResult?.success_probability || 0,
  recommendation: $json.priorityResult?.recommendation || 'Review',
  ice_impact: $json.priorityResult?.ice_score?.impact || 0,
  ice_confidence: $json.priorityResult?.ice_score?.confidence || 0,
  ice_ease: $json.priorityResult?.ice_score?.ease || 0,

  // Roadmap Information
  mvp_features: safeJoin($json.roadmapResult?.mvp_features),
  mvp_timeline: $json.roadmapResult?.mvp_timeline || '',
  phase2_features: safeJoin($json.roadmapResult?.phase2_features),
  phase2_timeline: $json.roadmapResult?.phase2_timeline || '',
  phase3_features: safeJoin($json.roadmapResult?.phase3_features),
  phase3_timeline: $json.roadmapResult?.phase3_timeline || '',
  dependencies: safeJoin($json.roadmapResult?.dependencies?.map(d => `${d.prerequisite} → ${d.dependent}`)),
  critical_path: safeJoin($json.roadmapResult?.critical_path),
  mvp_success_metrics: safeJoin($json.roadmapResult?.mvp_success_metrics),
  team_requirements_mvp: safeJoin($json.roadmapResult?.team_requirements?.mvp_phase),
  team_requirements_scale: safeJoin($json.roadmapResult?.team_requirements?.scaling_phase),
  resource_allocation: extractNestedData($json.roadmapResult?.resource_allocation),

  // Executive Summary & Output
  executive_summary: $json.outputResult?.executive_summary || '',
  final_recommendation: $json.outputResult?.recommendation || '',
  key_insights: safeJoin($json.outputResult?.key_insights),
  immediate_next_steps: safeJoin($json.outputResult?.immediate_next_steps),
  success_metrics: safeJoin($json.outputResult?.success_metrics),
  decision_timeline: $json.outputResult?.decision_timeline || '',

  // Business Case
  opportunity_size: $json.outputResult?.business_case?.opportunity_size || '',
  competitive_advantage: $json.outputResult?.business_case?.competitive_advantage || '',
  resource_investment: $json.outputResult?.business_case?.resource_investment || '',
  expected_roi: $json.outputResult?.business_case?.expected_roi || '',
  strategic_alignment: $json.outputResult?.strategic_alignment || '',

  // Risk Management
  risk_mitigation_strategies: safeJoin($json.outputResult?.risk_mitigation_plan?.map(r => `${r.risk}: ${r.mitigation}`)),
  high_priority_risks: safeJoin($json.outputResult?.risk_mitigation_plan?.filter(r => r.impact === 'High')?.map(r => r.risk)),

  // Input Constraints & Context
  original_constraints: $json.constraints || '',
  timeline_preference: $json.timelinePreference || '',

  // Analysis Metadata
  total_features_identified: (($json.roadmapResult?.mvp_features?.length || 0) + 
                              ($json.roadmapResult?.phase2_features?.length || 0) + 
                              ($json.roadmapResult?.phase3_features?.length || 0)),
  analysis_confidence: $json.priorityResult?.confidence_level || 'Medium',

  // Formatted Summaries for Quick Review
  quick_summary: `${$json.priorityResult?.priority_tier || 'Medium'} Priority (Score: ${$json.priorityResult?.calculated_score || 0}) - ${$json.outputResult?.recommendation || 'Review'}`,
  roadmap_summary: $json.roadmapResult?.roadmap_summary || 'Roadmap analysis pending',
  overall_assessment: `Market: ${$json.marketResult?.attractiveness_score || 0}/10, Priority: ${$json.priorityResult?.priority_tier || 'Medium'}, Recommendation: ${$json.outputResult?.recommendation || 'Review'}`
};

// Add workflow metadata
finalOutput.workflow_version = '2.0';
finalOutput.agent_model = 'gemini-2.0-flash-exp';
finalOutput.processing_status = 'Complete';

return [{ json: finalOutput }];
